import React, { useState } from 'react';
import './DonationPortal.css'; // Import the CSS file for styling

const initialEvents = [
  {
    id: 1,
    title: 'Annual Alumni Meetup',
    date: '2024-10-01',
    location: 'Grand Hall, City Center',
    description: 'Join us for the annual alumni meetup to reconnect and network.',
  },
  {
    id: 2,
    title: 'Scholarship Fundraiser',
    date: '2024-11-15',
    location: 'Community Center',
    description: 'Help us raise funds for student scholarships and support their education.',
  },
  // Add more events as needed
];

const DonationPortal = () => {
  const [events, setEvents] = useState(initialEvents);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const handleDonateClick = (event) => {
    setSelectedEvent(event);
  };

  const handlePayClick = () => {
    alert('Thank you for your donation!');
    setSelectedEvent(null);
  };

  return (
    <div className="donation-portal">
      <div className="event-list">
        {events.map((event) => (
          <div key={event.id} className="event-box">
            <h3 className="event-title">{event.title}</h3>
            <p className="event-date">{event.date}</p>
            <p className="event-location">{event.location}</p>
            <button
              className="donate-button"
              onClick={() => handleDonateClick(event)}
            >
              Donate
            </button>
          </div>
        ))}
      </div>
      {selectedEvent && (
        <div className="donation-details">
          <h2>{selectedEvent.title}</h2>
          <p>Date: {selectedEvent.date}</p>
          <p>Location: {selectedEvent.location}</p>
          <p>{selectedEvent.description}</p>
          <button className="pay-button" onClick={handlePayClick}>
            Pay
          </button>
        </div>
      )}
    </div>
  );
};

export default DonationPortal;
