import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Registration from './components/Registration';
import DonationPortal from './components/DonationPortal';
import NetworkingHub from './components/NetworkingHub';
import JobPortal from './components/JobPortal';
import AlumniDirectory from './components/AlumniDirectory';
import SuccessStories from './components/SuccessStories';
import EventsReunions from './components/EventsReunions';
import FeedbackSurveys from './components/FeedbackSurveys';
import Signup from './components/Signup';
import Login from './components/Login';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [userType, setUserType] = useState(null); // null | 'alumni' | 'student'

    const handleLogin = () => {
        setIsAuthenticated(true);
    };

    const handleSignup = () => {
        setIsAuthenticated(true);
    };

    if (!isAuthenticated) {
        return (
            <Router>
                <Routes>
                    <Route path="/" element={<SelectionPage setUserType={setUserType} />} />
                    {userType === 'alumni' && (
                        <Route path="/signup" element={<AlumniSignup onLoginClick={handleLogin} onSignupSubmit={handleSignup} />} />
                    )}
                    {userType === 'student' && (
                        <Route path="/signup" element={<StudentSignup onLoginClick={handleLogin} onSignupSubmit={handleSignup} />} />
                    )}
                    <Route path="/login" element={<Login onLoginSubmit={handleLogin} />} />
                </Routes>
            </Router>
        );
    }

    return (
        <Router>
            <Navbar />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/registration" element={<Registration />} />
                <Route path="/donation-portal" element={<DonationPortal />} />
                <Route path="/networking-hub" element={<NetworkingHub />} />
                <Route path="/job-portal" element={<JobPortal />} />
                <Route path="/alumni-directory" element={<AlumniDirectory />} />
                <Route path="/success-stories" element={<SuccessStories />} />
                <Route path="/events-reunions" element={<EventsReunions />} />
                <Route path="/feedback-surveys" element={<FeedbackSurveys />} />
            </Routes>
        </Router>
    );
}

export default App;
