import './Home.css';
import image1 from '../assets/image1.jpeg';
import image2 from '../assets/image2.jpeg';
import image3 from '../assets/image3.jpeg';
import { FaCalendarAlt, FaStar, FaUsers, FaDonate } from 'react-icons/fa';

const eventImages = [
    { image: image, title: "Event 1", date: "01 Jan 2023", icon: <FaCalendarAlt /> },
    { image: image, title: "Event 2", date: "15 Feb 2023", icon: <FaStar /> },
    { image: image, title: "Event 3", date: "10 Mar 2023", icon: <FaUsers /> },
    { image: image, title: "Event 4", date: "25 Apr 2023", icon: <FaDonate /> },
    { image: image, title: "Event 5", date: "05 May 2023", icon: <FaCalendarAlt /> },
    { image: image, title: "Event 6", date: "20 Jun 2023", icon: <FaStar /> },
    { image: image, title: "Event 7", date: "15 Jul 2023", icon: <FaUsers /> },
    { image: image, title: "Event 8", date: "30 Aug 2023", icon: <FaDonate /> },
    { image: image, title: "Event 9", date: "10 Sep 2023", icon: <FaCalendarAlt /> },
    { image: image, title: "Event 10", date: "25 Oct 2023", icon: <FaStar /> },
];


function Home() {
    const [homeContent, setHomeContent] = useState(initialHomeContent);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000);

        return () => clearInterval(timer);
    }, []);

    const [currentIndex, setCurrentIndex] = useState(0);

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? eventImages.length - 1 : prevIndex - 1
        );
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === eventImages.length - 1 ? 0 : prevIndex + 1
        );
    };

    return (
        <div className="home">
            <h1>{homeContent.title}</h1>
            <p>{homeContent.subtitle}</p>
            <div className="slideshow">
                <img src={images[currentImageIndex]} alt="Slideshow" />
            </div>
            <div className="event-section">
                {eventImages.map((event, index) => (
                    <div key={index} className="event-box">
                        {event.icon} {/* Display the icon */}
                        <img src={event.image} alt={event.title} />
                        <h3>{event.title}</h3>
                        <p>{event.date}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}
export default Home;
