import React, { useState } from 'react';
import './Home.css';
import image1 from '../assets/image1.jpeg';
import image2 from '../assets/image2.jpeg';
import image3 from '../assets/image3.jpeg';
import image4 from '../components/education-background.jpg.jpeg';

const eventImages = [
    { image: image4, title: "Event 1", date: "01 Jan 2023" },
    { image: image4, title: "Event 2", date: "15 Feb 2023" },
    { image: image4, title: "Event 3", date: "10 Mar 2023" },
    { image: image4, title: "Event 4", date: "25 Apr 2023" },
    { image: image4, title: "Event 5", date: "05 May 2023" },
    { image: image4, title: "Event 6", date: "20 Jun 2023" },
    { image: image4, title: "Event 7", date: "15 Jul 2023" },
    { image: image4, title: "Event 8", date: "30 Aug 2023" },
    { image: image4, title: "Event 9", date: "10 Sep 2023" },
    { image: image4, title: "Event 10", date: "25 Oct 2023" },
];
const images = [image1, image2, image3];



function Home() {
    const [homeContent] = useState({
        title: "Welcome to the GEC Alumni Association Platform",
        subtitle: "Connecting Alumni, Supporting Growth, and Fostering Success.",
    });

    
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000);

        return () => clearInterval(timer);
    }, []);

    const [currentIndex, setCurrentIndex] = useState(0);

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? eventImages.length - 1 : prevIndex - 1
        );
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === eventImages.length - 1 ? 0 : prevIndex + 1
        );
    };

    return (
        <div className="home">
            <h1>{homeContent.title}</h1>
            <p>{homeContent.subtitle}</p>
            <div className="slideshow">
                <img src={image1} alt="Slideshow" />
            </div>

            <div className="event-slider">
                <button className="prev" onClick={handlePrevClick}>❮</button>
                <div className="event-boxes">
                    {eventImages.slice(currentIndex, currentIndex + 5).map((event, index) => (
                        <div key={index} className="event-box">
                            <img src={event.image} alt={event.title} />
                            
                        </div>
                    ))}
                </div>
                <button className="next" onClick={handleNextClick}>❯</button>
            </div>
        </div>
    );
}

export default Home;
