import React, { useState, useEffect } from 'react';
import './Home.css';
import image1 from '../assets/image1.jpeg';
import image2 from '../assets/image2.jpeg';
import image3 from '../assets/image3.jpeg';
import { FaHome, FaUserGraduate, FaDonate, FaNetworkWired, FaBriefcase, FaUsers, FaStar, FaCalendarAlt, FaCommentDots, FaUserCircle } from 'react-icons/fa';

const images = [image1, image2, image3];
const eventIcons = [
    { icon: FaHome, title: "Event 1", date: "01 Jan 2023" },
    { icon: FaUserGraduate, title: "Event 2", date: "15 Feb 2023" },
    { icon: FaDonate, title: "Event 3", date: "10 Mar 2023" },
    { icon: FaNetworkWired, title: "Event 4", date: "25 Apr 2023" },
    { icon: FaBriefcase, title: "Event 5", date: "05 May 2023" },
    { icon: FaUsers, title: "Event 6", date: "20 Jun 2023" },
    { icon: FaStar, title: "Event 7", date: "15 Jul 2023" },
    { icon: FaCalendarAlt, title: "Event 8", date: "30 Aug 2023" },
    { icon: FaCommentDots, title: "Event 9", date: "10 Sep 2023" },
    { icon: FaHome, title: "Event 10", date: "25 Oct 2023" },
];

const EventBox = ({ icon: Icon, title, date }) => (
    <div className="event-box">
        <div className="icon-container">
            <Icon size={50} color="#2243bd" />
        </div>
        <h3>{title}</h3>
        <p>{date}</p>
    </div>
);

function Home() {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // State to manage login status
    const [showLogin, setShowLogin] = useState(false); // State to manage login page visibility
    const [showSignup, setShowSignup] = useState(false); // State to manage signup page visibility
    const [homeContent, setHomeContent] = useState({
        title: "Welcome to the GEC Alumni Association Platform",
        subtitle: "Connecting Alumni, Supporting Growth, and Fostering Success.",
    });
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000);

        return () => clearInterval(timer);
    }, []);

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? eventIcons.length + 1 : prevIndex - 1
        );
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === eventIcons.length + 1 ? 0 : prevIndex + 1
        );
    };

    const handleLoginClick = () => {
        setShowLogin(true);
        setShowSignup(false);
    };

    const handleSignupClick = () => {
        setShowSignup(true);
        setShowLogin(false);
    };

    if (showLogin) {
        return (
            <div className="login-page">
                {/* Replace this with your actual Login component */}
                <h2>Login Page</h2>
                <button onClick={() => setShowLogin(false)}>Back to Home</button>
            </div>
        );
    }

    if (showSignup) {
        return (
            <div className="signup-page">
                {/* Replace this with your actual Signup component */}
                <h2>Signup Page</h2>
                <button onClick={() => setShowSignup(false)}>Back to Home</button>
            </div>
        );
    }

    return (
        <div className="home-page">
            <div className="header">
                <div className="auth-buttons">
                    {isLoggedIn ? (
                        <FaUserCircle size={30} />
                    ) : (
                        <>
                            <button className="login-button" onClick={handleLoginClick}>Login</button>
                            <button className="signup-button" onClick={handleSignupClick}>Signup</button>
                        </>
                    )}
                </div>
            </div>

            <div className="home">
                <h1>{homeContent.title}</h1>
                <p>{homeContent.subtitle}</p>
                <div className="slideshow">
                    <img src={images[currentImageIndex]} alt="Slideshow" />
                </div>
                <div className="college-description">
                    <h3>Established in 2004, Government Engineering College, Gandhinagar (GEC-Gn) takes pride in its highly motivated students...</h3>
                </div>
                <div className="event-section">
                    <button className="arrow-button" onClick={handlePrevClick}>&lt;</button>
                    <div className="event-container">
                        {eventIcons.slice(currentIndex, currentIndex + 5).map((event, index) => (
                            <EventBox
                                key={index}
                                icon={event.icon}
                                title={event.title}
                                date={event.date}
                            />
                        ))}
                    </div>
                    <button className="arrow-button" onClick={handleNextClick}>&gt;</button>
                </div>
            </div>
        </div>
    );
}

export default Home;
