import React, { useState, useEffect } from 'react';
import './Home.css';

// Placeholder data (can be fetched from a database later)
const initialHomeContent = {
    title: "Welcome to the GEC Alumni Association Platform",
    subtitle: "Connecting Alumni, Supporting Growth, and Fostering Success.",
};

// Array of image URLs for the slideshow
const images = [
    'D:\allwebsites\alumni-association\src\components\download (1).jpeg', // Replace with actual image URLs
    'D:\allwebsites\alumni-association\src\components\download (2).jpeg',
    'D:\allwebsites\alumni-association\src\components\download (3).jpeg'
];

function Home() {
    const [homeContent, setHomeContent] = useState(initialHomeContent);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        // Future implementation to fetch data from SQL database
        // fetch('/api/home-content')
        //     .then(response => response.json())
        //     .then(data => setHomeContent(data))
        //     .catch(error => console.error('Error fetching home content:', error));

        // Timer to change images every 2 seconds
        const timer = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000);

        // Cleanup timer on component unmount
        return () => clearInterval(timer);
    }, []);

    return (
        <div className="home">
            <h1>{homeContent.title}</h1>
            <p>{homeContent.subtitle}</p>
            <div className="slideshow">
                <img src={images[currentImageIndex]} alt="Slideshow" />
            </div>
        </div>
    );
}

export default Home;
