import React, { useState, useEffect } from 'react';
import './Home.css';
/*import image1 from './assets/download(1).jpeg';
import image2 from './assets/download(2).jpeg';
import image3 from './assets/download(3).jpeg';*/

const initialHomeContent = {
    title: "Welcome to the GEC Alumni Association Platform",
    subtitle: "Connecting Alumni, Supporting Growth, and Fostering Success.",
};

const images = [image1, image2, image3];

function Home() {
    const [homeContent, setHomeContent] = useState(initialHomeContent);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000);

        return () => clearInterval(timer);
    }, []);

    return (
        <div className="home">
            <h1>{homeContent.title}</h1>
            <p>{homeContent.subtitle}</p>
            <div className="slideshow">
                <img src={images[currentImageIndex]} alt="Slideshow" />
            </div>
        </div>
    );
}

export default Home;
