import React, { useState } from 'react';
import './JobPortal.css'; // Import the CSS file for styling

const initialJobs = [
  {
    id: 1,
    title: 'Software Engineer',
    company: 'TechCorp',
    location: 'San Francisco, CA',
    description: 'Looking for an experienced Software Engineer to join our team.',
  },
  {
    id: 2,
    title: 'Product Manager',
    company: 'Innovate Inc.',
    location: 'New York, NY',
    description: 'Seeking a skilled Product Manager to lead product development.',
  },
  // Add more jobs as needed
];

const JobPortal = () => {
  const [jobs, setJobs] = useState(initialJobs);
  const [search, setSearch] = useState('');
  const [newJob, setNewJob] = useState({ title: '', company: '', location: '', description: '' });

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleAddJob = () => {
    if (newJob.title && newJob.company && newJob.location && newJob.description) {
      setJobs([...jobs, { ...newJob, id: jobs.length + 1 }]);
      setNewJob({ title: '', company: '', location: '', description: '' });
    }
  };

  const filteredJobs = jobs.filter((job) =>
    job.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="job-portal">
      <div className="header">
        <button className="add-job-button" onClick={handleAddJob}>Add Job</button>
        <input
          type="text"
          placeholder="Search Jobs"
          value={search}
          onChange={handleSearch}
          className="search-input"
        />
      </div>
      <div className="job-list">
        {filteredJobs.map((job) => (
          <div key={job.id} className="job-box">
            <h3 className="job-title">{job.title}</h3>
            <p className="job-company">{job.company}</p>
            <p className="job-location">{job.location}</p>
            <p className="job-description">{job.description}</p>
            <div className="job-buttons">
              <button className="apply-button">Apply</button>
              <button className="view-details-button">View Details</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default JobPortal;
